import { useState } from 'react'

function useRerender(): [boolean, () => void] {
  const [forceRerender, setForceRerender] = useState(false)

  return [
    forceRerender,
    () => {
      setForceRerender(state => !state)
    },
  ]
}

export { useRerender }
