import { Card, Topic } from '@flashcards/core'
import { GetTopicService, GetTopicUseCase } from '../usecase/GetTopic'

class GetTopic implements GetTopicUseCase {
  private readonly service: GetTopicService

  constructor(service: GetTopicService) {
    this.service = service
  }

  async getTopic(token: string, topicId: string): Promise<Topic> {
    const topicData = await this.service.getTopic(token, topicId)
    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(card => new Card(card)),
    })
  }
}

export { GetTopic }
