import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import Grid from '@material-ui/core/Grid'
import Typography from '@material-ui/core/Typography'
import { TopicData } from '@flashcards/client'
import { Pages } from '@/models'
import { Header } from '@/components/Header'
import { useToken } from '@/hooks/useToken'
import { useUseCases } from '@/hooks/useUseCases'
import { Flashcard } from '@/components/Flashcard'
import { useStyles } from './[topicId].style'

const Topic = () => {
  const router = useRouter()
  const styles = useStyles()
  const token = useToken()
  const { getTopic } = useUseCases()
  const [topic, setTopic] = useState<TopicData | null>(null)
  const { topicId } = router.query as { topicId: string }

  useEffect(() => {
    let isMounted = true

    if (token) {
      getTopic(token, topicId).then(data => {
        if (isMounted) {
          setTopic(data)
        }
      })
    }
    return () => {
      isMounted = false
    }
  }, [token])

  if (!topic) {
    return <div>Loading...</div>
  }

  return (
    <>
      <Header />
      <main data-testid="topic-page">
        <Grid container direction="column" spacing={2} className={styles.list}>
          <Grid item>
            <Typography variant="h1" className={styles.title}>
              {topic.name}
            </Typography>
          </Grid>
          {/* <Grid item>
            <NewCardForm
              onAdd={async (question, answer) => {
                await addCard({ question, answer }, topicId, user.uid)
              }}
            />
          </Grid> */}
          <Grid item>
            <Grid container direction="row" spacing={2}>
              {topic.cards.map(card => (
                <Grid item key={card.id} xs={12} sm={6} md={3}>
                  <Flashcard front={card.front} containerClassName={styles.flashCard}>
                    {card.back}
                  </Flashcard>
                </Grid>
              ))}
            </Grid>
          </Grid>
        </Grid>
      </main>
    </>
  )
}

Topic.displayName = Pages.Topic

export default Topic
