import * as React from 'react'
import Head from 'next/head'
import { firebase } from '@flashcards/client'

function LoginFirebaseUI(): React.ReactElement {
  const [state, setState] = React.useState(false)

  React.useLayoutEffect(() => {
    if (!document.getElementById('firebaseui-js')) {
      setState(prevState => !prevState)
    } else {
      const uiConfig = {
        signInOptions: [firebase.auth.GoogleAuthProvider.PROVIDER_ID],
        signInFlow: 'popup',
        signInSuccessUrl: '/',
      }
      // eslint-disable-next-line @typescript-eslint/ban-ts-comment
      // @ts-ignore
      const ui = new firebaseui.auth.AuthUI(firebase.auth())
      ui.start('#firebaseui-auth-container', uiConfig)
    }
  }, [state])
  return (
    <>
      <Head>
        <script
          id="firebaseui-js"
          src="https://www.gstatic.com/firebasejs/ui/4.6.1/firebase-ui-auth.js"
        ></script>
        <link
          type="text/css"
          rel="stylesheet"
          href="https://www.gstatic.com/firebasejs/ui/4.6.1/firebase-ui-auth.css"
        />
      </Head>
      <div id="firebaseui-auth-container"></div>
    </>
  )
}

export { LoginFirebaseUI }
