import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import Grid from '@material-ui/core/Grid'
import { GetTopicsUseCase, AddTopicUseCase } from '@flashcards/client'
import { Topic } from '@flashcards/core'
import { useAuth } from 'contexts/auth'
import { Pages } from 'models'
import { Header } from 'components/Header'
import { NewTopicForm } from 'components/NewTopicForm'
import { TopicsList } from 'components/TopicsList'
import { Routes } from 'models/Routes'

import { useStyles } from './Home.styles'

function Home({ getTopics, addTopic }: { getTopics: GetTopicsUseCase; addTopic: AddTopicUseCase }) {
  const router = useRouter()
  const { user, loading, getToken } = useAuth()
  const styles = useStyles()
  const [topics, setTopics] = useState<Topic[]>([])

  useEffect(() => {
    let isMounted = true
    ;(async () => {
      const token = await getToken()
      if (token) {
        const topics = await getTopics({ token })
        if (isMounted) {
          setTopics(topics)
        }
      }
    })()

    return () => {
      isMounted = false
    }
  }, [user])

  if (loading) {
    return <div>Loading...</div>
  }

  if (!user) {
    return router.push(Routes.login())
  }

  return (
    <>
      <Header />
      <Grid container direction="column" className={styles.container}>
        <Grid item>
          <NewTopicForm onTopicSubmitted={({ name }) => addTopic(user.token, { name })} />
        </Grid>
        <Grid item>
          <TopicsList
            items={topics}
            onItemRemoved={(topicId: string) => {
              // removeTopic(user.uid, topicId)
            }}
          />
        </Grid>
      </Grid>
    </>
  )
}

Home.displayName = Pages.Home

export { Home }
