import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import Grid from '@material-ui/core/Grid'
import { GetTopicsObserverUseCase, AddTopicUseCase } from '@flashcards/client'
import { Topic } from '@flashcards/core'
import { useAuth } from 'contexts/auth'
import { Pages } from 'models'
import { Header } from 'components/Header'
import { NewTopicForm } from 'components/NewTopicForm'
import { TopicsList } from 'components/TopicsList'
import { Routes } from 'models/Routes'
import { makeStyles, createStyles } from '@material-ui/core/styles'
import { useQuery, gql } from '@apollo/client'

const useStyles = makeStyles(theme =>
  createStyles({
    container: {
      padding: theme.spacing(3),
    },
    gridList: {
      marginTop: theme.spacing(3),
    },
  }),
)

const QUERY = gql`
  query Topics {
    topics {
      id
      name
    }
  }
`

function Home({
  getTopicsObserver,
  addTopic,
}: {
  getTopicsObserver: GetTopicsObserverUseCase
  addTopic: AddTopicUseCase
}) {
  const router = useRouter()
  const { user, loading } = useAuth()
  const styles = useStyles()
  const [topics, setTopics] = useState<Topic[]>([])

  const { data, error } = useQuery(QUERY, {
    context: {
      headers: {
        authentication: user?.token,
      },
    },
    skip: !user?.token,
  })

  useEffect(() => {
    setTopics(data?.topics.map(topicData => new Topic(topicData)) ?? [])
  }, [data])
  console.log('data', data, error)

  // useEffect(() => {
  //   let isMounted = true

  //   if (user?.token) {
  //     return getTopicsObserver({ token: user.token }).subscribe(topics => {
  //       console.log('subscribe')
  //       if (isMounted) {
  //         setTopics(topics)
  //       }
  //     })
  //   }

  //   return () => {
  //     isMounted = false
  //   }
  // }, [user])

  if (loading) {
    return <div>Loading...</div>
  }

  if (!user) {
    return router.push(Routes.login())
  }

  console.log(user)

  return (
    <>
      <Header />
      <Grid container direction="column" className={styles.container}>
        <Grid item>
          <NewTopicForm onTopicAdded={({ name }) => addTopic(user.token, { name })} />
        </Grid>
        <Grid item>
          <TopicsList
            items={topics}
            onItemRemoved={(topicId: string) => {
              // removeTopic(user.uid, topicId)
            }}
          />
        </Grid>
      </Grid>
    </>
  )
}

Home.displayName = Pages.Home

export default Home
