import { Card } from '@flashcards/core'
import { AddCardInput, AddCardService, AddCardUseCase } from '../usecase'

class AddCard implements AddCardUseCase {
  private readonly service: AddCardService

  constructor(service: AddCardService) {
    this.service = service
  }

  async addCard(token: string, input: AddCardInput): Promise<Card> {
    const cardData = await this.service.addCard(token, input)
    return new Card(cardData)
  }
}

export { AddCard }
