import * as React from 'react'
import { useRouter } from 'next/router'
import { Pages, Routes } from '../../models'
import {
  LoginProvidersData,
  LoginWithProviderUseCase,
} from '@flashcards/client'

type Props = {
  loginWithProvider: LoginWithProviderUseCase
}

function Login({ loginWithProvider }: Props) {
  const router = useRouter()

  return (
    <main>
      <h1>Login</h1>
      {Object.keys(LoginProvidersData).map(provider => (
        <button
          key={provider}
          onClick={() => {
            loginWithProvider(provider as LoginProvidersData).then(() => {
              router.push(Routes.home())
            })
          }}
        >
          {provider}
        </button>
      ))}
    </main>
  )
}

Login.displayName = Pages.Login

export { Login }
