import { GetTopicService } from './GetTopicService'
import { GetTopicUseCase } from './GetTopicUseCase'

function createGetTopicUseCase(service: GetTopicService): GetTopicUseCase {
  return function (token, topicId) {
    return service.getTopic(token, topicId)
  }
}

export { createGetTopicUseCase }
