import { withAuth } from '../hocs/withAuth'
import { withRedirectToLogin } from '../hocs/withRedirectToLogin'
import { Pages } from '../models'

type Params = React.ElementType & { displayName?: string }

const addDecorators = (Component: Params): React.ElementType => {
  switch (Component.displayName) {
    case Pages.Home:
      return withAuth(withRedirectToLogin(Component))
    default:
      return Component
  }
}

export { addDecorators }
