import { Card, Topic, User } from '@flashcards/core'
import { AuthService } from '../service/AuthService'
import { FirebaseService } from '../service/firebase/service'
import { GraphqlService } from '../service/graphql/service'
import { Observer } from '../service/Observer'
import { RepositoryService } from '../service/RepositoryService'
import { AddCardInput, AddCardUseCase } from './AddCard'
import { AddTopicInput, AddTopicUseCase } from './AddTopic'
import { GetCurrentUserUseCase } from './GetCurrentUser'
import { GetTopicUseCase } from './GetTopic'
import { GetTopicsUseCase } from './GetTopics'
import { GetTopicsObserverUseCase } from './GetTopicsObserver'
import { GetUserTokenUseCase } from './GetUserToken'
import { Observable } from './protocols'
import { RemoveTopicUseCase } from './RemoveTopic'
import { UserObserverInput, UserObserverUseCase } from './UserObserver'

class UseCases
  implements
    AddCardUseCase,
    AddTopicUseCase,
    GetCurrentUserUseCase,
    GetTopicUseCase,
    GetTopicsUseCase,
    GetTopicsObserverUseCase,
    GetUserTokenUseCase,
    RemoveTopicUseCase,
    UserObserverUseCase {
  private readonly firebaseService: AuthService
  private readonly graphqlService: RepositoryService

  constructor(firebaseService: AuthService, graphqlService: RepositoryService) {
    this.firebaseService = firebaseService
    this.graphqlService = graphqlService
  }

  async addCard(token: string, input: AddCardInput): Promise<Card> {
    const cardData = await this.graphqlService.addCard(token, input)
    return new Card(cardData)
  }

  async addTopic(token: string, data: AddTopicInput): Promise<Topic> {
    const topicData = await this.graphqlService.addTopic(token, data)
    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(card => new Card(card)),
    })
  }

  getCurrentUser(): Promise<User> {
    return this.firebaseService.getCurrentUser()
  }

  async getTopic(token: string, topicId: string): Promise<Topic> {
    const topicData = await this.graphqlService.getTopic(token, topicId)
    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(card => new Card(card)),
    })
  }

  async getTopics(token: string): Promise<Topic[]> {
    const topics = await this.graphqlService.getTopics(token)
    return topics.map(
      topicData =>
        new Topic({
          id: topicData.id,
          name: topicData.name,
          cards: topicData.cards.map(card => new Card(card)),
        }),
    )
  }

  getTopicsObserver(token: string): Observable<Topic[]> {
    return new Observer(next => {
      const observer = this.graphqlService.getTopicsObserver(token)
      observer.subscribe(topicsData => {
        console.log(topicsData)
        next(
          topicsData.map(
            topicData =>
              new Topic({
                id: topicData.id,
                name: topicData.name,
                cards: topicData.cards.map(card => new Card(card)),
              }),
          ),
        )
      })
    })
  }

  getUserToken(): Promise<string> {
    return this.firebaseService.getUserToken()
  }

  removeTopic(token: string, topicId: string): Promise<boolean> {
    return this.graphqlService.removeTopic(token, topicId)
  }

  userObserver(callback: UserObserverInput): void {
    return this.firebaseService.userObserver(callback)
  }
}

export { UseCases }
