import { StrictMode } from 'react'
import type { AppProps } from 'next/app'
import CssBaseline from '@material-ui/core/CssBaseline'
import { getPageDependencies, addDecorators } from '../main'
import { ApolloProvider } from '@apollo/client'
import { ApolloClient, InMemoryCache } from '@apollo/client'

const client = new ApolloClient({
  uri: process.env.GRAPHQL_URL ?? 'http://localhost:4000/graphql',
  cache: new InMemoryCache(),
})
function MyApp({ Component, pageProps }: AppProps) {
  const pageDependencies = getPageDependencies(Component.displayName)
  const DecoratedComponent = addDecorators(Component)

  return (
    <StrictMode>
      <CssBaseline />
      <ApolloProvider client={client}>
        <DecoratedComponent {...pageProps} {...pageDependencies} />
      </ApolloProvider>
    </StrictMode>
  )
}

export default MyApp
