import { useState, useRef } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import AppBar from '@material-ui/core/AppBar'
import Avatar from '@material-ui/core/Avatar'
import Box from '@material-ui/core/Box'
import IconButton from '@material-ui/core/IconButton'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import Link from 'next/link'
import { useAuth } from '../../contexts/auth'
import { Routes } from '../../models'

const useStyles = makeStyles({
  userName: {
    flexGrow: 1,
  },
  title: {
    textDecoration: 'none',
    color: 'white',
  },
})

function Header() {
  const { user } = useAuth()
  const styles = useStyles()
  const [shouldShowPopover, setShouldShowPopover] = useState(false)

  return (
    <AppBar position="static">
      <Toolbar>
        <Typography component={Link} href={Routes.home()} className={styles.title}>
          Flashcards
        </Typography>
        <Box flexGrow={1} />
        {user && (
          <Box display="flex" alignItems="center">
            <IconButton>
              <Avatar src={user.photoUrl || undefined} alt={user.name} />
            </IconButton>
            <Typography className={styles.userName}>{user.name}</Typography>
          </Box>
        )}
      </Toolbar>
    </AppBar>
  )
}

export { Header }
