import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import Grid from '@material-ui/core/Grid'
import { GetTopicsObserverUseCase, AddTopicUseCase } from '@flashcards/client'
import { Topic } from '@flashcards/core'
import { useAuth } from 'contexts/auth'
import { Pages } from 'models'
import { Header } from 'components/Header'
import { NewTopicForm } from 'components/NewTopicForm'
import { TopicsList } from 'components/TopicsList'
import { Routes } from 'models/Routes'
import { makeStyles, createStyles } from '@material-ui/core/styles'

const useStyles = makeStyles(theme =>
  createStyles({
    container: {
      padding: theme.spacing(3),
    },
    gridList: {
      marginTop: theme.spacing(3),
    },
  }),
)

export { useStyles }

function Home({
  getTopicsObserver,
  addTopic,
}: {
  getTopicsObserver: GetTopicsObserverUseCase
  addTopic: AddTopicUseCase
}) {
  const router = useRouter()
  const { user, loading, getToken } = useAuth()
  const styles = useStyles()
  const [topics, setTopics] = useState<Topic[]>([])

  useEffect(() => {
    let isMounted = true
    const observer = getTopicsObserver({ token: user.token })

    return observer.subscribe(topics => {
      if (isMounted) {
        setTopics(topics)
      }
    })

    getToken().then(token => {
      if (token) {
        return getTopicsObserver({ token: user.token })
      }
    })

    if (user) {
    }

    return () => {
      isMounted = false
    }
  }, [user])

  if (loading) {
    return <div>Loading...</div>
  }

  if (!user) {
    return router.push(Routes.login())
  }

  console.log(user)

  return (
    <>
      <Header />
      <Grid container direction="column" className={styles.container}>
        <Grid item>
          <NewTopicForm onTopicSubmitted={({ name }) => addTopic(user.token, { name })} />
        </Grid>
        <Grid item>
          <TopicsList
            items={topics}
            onItemRemoved={(topicId: string) => {
              // removeTopic(user.uid, topicId)
            }}
          />
        </Grid>
      </Grid>
    </>
  )
}

Home.displayName = Pages.Home

export default Home
