import { useState, useEffect } from 'react'
import Grid from '@material-ui/core/Grid'
import { Topic } from '@flashcards/core'
import { useAuth } from 'contexts/auth'
import { Pages } from 'models'
import { Header } from 'components/Header'
import { NewTopicForm } from 'components/NewTopicForm'
import { TopicsList } from 'components/TopicsList'
import { useUseCases } from 'hooks/useUseCases'
import { useStyles } from './Home.styles'
import { withAuth } from 'hocs/withAuth'
import { withRedirectToLogin } from 'hocs/withRedirectToLogin'
import { useRerender } from 'hooks/useRerender'

const Home = () => {
  const { user, getToken } = useAuth()
  const styles = useStyles()
  const [topics, setTopics] = useState<Topic[]>([])
  const [token, setToken] = useState<string>('')
  const { getTopics, addTopic, removeTopic } = useUseCases()

  useEffect(() => {
    let isMounted = true
    ;(async () => {
      const token = await getToken()
      if (token && isMounted) {
        setToken(token)
      }
    })()

    return () => {
      isMounted = false
    }
  }, [user])

  useEffect(() => {
    let isMounted = true

    if (token) {
      const unsubscribe = getTopicsObserver(token).subscribe(topics => {
        if (isMounted) {
          setTopics(topics)
        }
      })

      return () => {
        isMounted = false
        unsubscribe()
      }
    }

    return () => {
      isMounted = false
    }
  }, [token])

  return (
    <>
      <Header />
      <Grid container direction="column" className={styles.container}>
        <Grid item>
          <NewTopicForm
            onTopicSubmitted={async ({ name }) => {
              await addTopic(token, { name })
            }}
          />
        </Grid>
        <Grid item>
          <TopicsList
            items={topics}
            onItemRemoved={async (topicId: string) => {
              await removeTopic(token, topicId)
            }}
          />
        </Grid>
      </Grid>
    </>
  )
}

Home.displayName = Pages.Home

const HomeDecorated = withAuth(withRedirectToLogin(Home))

export { HomeDecorated as Home }
