import * as React from 'react'
import type { UserData } from '@flashcards/client'
import { factory } from '@flashcards/client'

type State = {
  user: UserData | null
  loading: boolean
}

type Action = { type: 'loaded'; payload: UserData | null }

const userObserver = factory.createUserObserver()
const AuthContext = React.createContext<State>(null)

const useAuth = () => {
  return React.useContext(AuthContext)
}

const initialState = { user: null, loading: true }

function reducer(state: State, action: Action): State {
  if (action.type === 'loaded') {
    return {
      loading: false,
      user: action.payload,
    }
  }

  return state
}

const AuthProvider = ({ children }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState)

  React.useEffect(() => {
    userObserver(user => {
      dispatch({ type: 'loaded', payload: user })
    })
  }, [])

  return <AuthContext.Provider value={state}>{children}</AuthContext.Provider>
}

export { AuthProvider, useAuth }
