import { Card, Topic } from '@flashcards/core'
import { AddTopicService } from './AddTopicService'
import { AddTopicUseCase } from './AddTopicUseCase'

function createAddTopicUseCase(service: AddTopicService): AddTopicUseCase {
  return async function (token, data) {
    const topicData = await service.addTopic(token, data)

    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(cardData => new Card(cardData)),
    })
  }
}

export { createAddTopicUseCase }
