import { Card, Topic } from '@flashcards/core'
import { FirebaseService } from '../service/firebase/service'
import { GraphqlService } from '../service/graphql/service'
import { AddCardInput, AddCardUseCase } from './AddCard'
import { AddTopicInput, AddTopicUseCase } from './AddTopic'
import { GetTopicUseCase } from './GetTopic'

class UseCases implements AddCardUseCase, AddTopicUseCase, GetTopicUseCase {
  private readonly firebaseService: FirebaseService
  private readonly graphqlService: GraphqlService

  constructor(firebaseService: FirebaseService, graphqlService: GraphqlService) {
    this.firebaseService = firebaseService
    this.graphqlService = graphqlService
  }

  async addCard(token: string, input: AddCardInput): Promise<Card> {
    const cardData = await this.graphqlService.addCard(token, input)
    return new Card(cardData)
  }

  async addTopic(token: string, data: AddTopicInput): Promise<Topic> {
    const topicData = await this.graphqlService.addTopic(token, data)
    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(card => new Card(card)),
    })
  }

  async getTopic(token: string, topicId: string): Promise<Topic> {
    const topicData = await this.graphqlService.getTopic(token, topicId)
    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(card => new Card(card)),
    })
  }
}

export { UseCases }
