import { Card, Topic } from '@flashcards/core'
import { AddTopicInput, AddTopicService, AddTopicUseCase } from '../usecase'

class AddTopic implements AddTopicUseCase {
  private readonly service: AddTopicService

  constructor(service: AddTopicService) {
    this.service = service
  }

  async addTopic(token: string, data: AddTopicInput): Promise<Topic> {
    const topicData = await this.service.addTopic(token, data)
    return new Topic({
      id: topicData.id,
      name: topicData.name,
      cards: topicData.cards.map(card => new Card(card)),
    })
  }
}

export { AddTopic }
