import { Card } from '@flashcards/core'
import { AddCardService } from './AddCardService'
import { AddCardUseCase } from './AddCardUseCase'

function createAddCardUseCase(service: AddCardService): AddCardUseCase {
  return async function (token, data) {
    const cardData = await service.addCard(token, data)

    return new Card(cardData)
  }
}

export { createAddCardUseCase }
