import firebase from 'firebase/app'
import 'firebase/auth'
import {
  GetCurrentUserService,
  GetUserTokenService,
  UserData,
  UserObserverService,
} from '../../usecase'

class FirebaseService implements UserObserverService, GetCurrentUserService, GetUserTokenService {
  private readonly firebase: firebase.app.App

  constructor(options: unknown) {
    this.firebase = firebase.initializeApp(options)
  }

  getUserToken(): Promise<string> {
    const firebaseUser = this.firebase.auth().currentUser

    if (!firebaseUser) {
      return Promise.resolve(null)
    }

    return firebaseUser.getIdToken(true)
  }

  async getCurrentUser(): Promise<UserData> {
    const firebaseUser = this.firebase.auth().currentUser

    if (!firebaseUser) {
      return null
    }

    return {
      email: firebaseUser.email ?? '',
      emailVerified: firebaseUser.emailVerified ?? false,
      name: firebaseUser.displayName ?? '',
      photoUrl: firebaseUser.photoURL ?? '',
      uid: firebaseUser.uid,
    }
  }

  userObserver(callback: (user: UserData) => void): void {
    this.firebase.auth().onAuthStateChanged(async firebaseUser => {
      if (!firebaseUser) {
        return callback(null)
      }

      callback({
        email: firebaseUser.email ?? '',
        emailVerified: firebaseUser.emailVerified ?? false,
        name: firebaseUser.displayName ?? '',
        photoUrl: firebaseUser.photoURL ?? '',
        uid: firebaseUser.uid,
      })
    })
  }
}

export { FirebaseService }
